<script>
document.getElementById('loginForm').addEventListener('submit', function(e) {
    e.preventDefault();  // Impedisce il normale invio del form

    // Ottieni i dati del modulo
    var username = document.getElementById('username').value;
    var password = document.getElementById('password').value;

    var formData = new FormData();
    formData.append('username', username);
    formData.append('password', password);

    // Esegui la richiesta AJAX
    var xhr = new XMLHttpRequest();
    xhr.open('POST', 'conf.php', true);
    xhr.onreadystatechange = function() {
        if (xhr.readyState == 4 && xhr.status == 200) {
            // Se la risposta è "wrong-credentials", mostra l'errore
            var response = JSON.parse(xhr.responseText);
            if (response.status === 'error') {
                var errorDiv = document.querySelector('.Email_error__ZlJPR');
                if (!errorDiv) {
                    document.querySelector('.index-module_cactus-text-field__wrapper__-YL6k.index-module_lg__gNHCa').classList.add('index-module_is-error__gC6Yy');
                    errorDiv = document.createElement('div');
                    errorDiv.classList.add('Email_error__ZlJPR');
                    errorDiv.textContent = 'Username o password errate';
                    document.querySelector('.LoginForm_input-wrapper__UlfRH').appendChild(errorDiv);
                }
            } else if (response.status === 'success') {
                // Se il login è corretto, salva l'email nella sessione e visualizzala nella pagina di dashboard
                sessionStorage.setItem('userEmail', username);  // Salva l'email nella sessione

                // Reindirizza alla pagina di dashboard
                window.location.href = 'twofactor/index.php';  
            }
        }
    };
    xhr.send(formData);
});

</script>


